import { useState, useEffect } from "react";
import axios from "axios";
import "../App.css";

function Login() {
  const [flightNumber, setFlightNumber] = useState(null);
  const [departureTime, setDepartureTime] = useState(null);
  const [arrivalTime, setArrivalTime] = useState(null);
  const [economySeats, setEconomySeats] = useState(null);
  const [arrivalDate, setArrivalDate] = useState(null);
  const [departureDate, setDepartureDate] = useState(null);
  const [businessSeats, setBusinessSeats] = useState(null);
  const [airport, setAirport] = useState(null);

  const [listOfFlights, setListOfFlights] = useState([]);

  const creFlight = () => {
    if (
      flightNumber == null ||
      departureTime == null ||
      arrivalTime == null ||
      departureDate == null ||
      arrivalDate == null ||
      economySeats == null ||
      businessSeats == null ||
      airport == null
    ) {
      alert("Please fill all fields!!!");
    } else {
      axios
        .post("http://localhost:8000/admin/createFlight", {
          flightNumber: flightNumber,
          departureTime: departureTime,
          arrivalTime: arrivalTime,
          numberofEconomySeats: economySeats,
          arrivalDate: arrivalDate,
          departureDate: departureDate,
          numberofBusinessSeats: businessSeats,
          airport: airport,
        })
        .then(() => {
          setListOfFlights([
            ...listOfFlights,
            {
              flightNumber: flightNumber,
              departureTime: departureTime,
              arrivalTime: arrivalTime,
              numberofEconomySeats: economySeats,
              arrivalDate: arrivalDate,
              departureDate: departureDate,
              numberofBusinessSeats: businessSeats,
              airport: airport,
            },
          ]);
        })
        .catch((err) => {});
    }
  };

  useEffect(() => {
    axios
      .get("http://localhost:8000/admin/showFlight")
      .then((result) => {
        setListOfFlights(result.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  return (
    <div>
      <div className="CreFlight">
        <h1>Create Flights</h1>
      </div>
      <div className="App">
        <div className="Creinputs">
          <div>
            <div>
              <input
                type="number"
                placeholder="Flight Number"
                onChange={(event) => {
                  setFlightNumber(event.target.value);
                }}
              />
              Departure Time
              <input
                type="time"
                onChange={(event) => {
                  setDepartureTime(event.target.value);
                }}
              />
              Arrival Time
              <input
                type="time"
                onChange={(event) => {
                  setArrivalTime(event.target.value);
                }}
              />
              <input
                type="text"
                placeholder="Airport"
                onChange={(event) => {
                  setAirport(event.target.value);
                }}
              />
            </div>
            <div>
              <input
                type="number"
                placeholder="Economy Seats"
                onChange={(event) => {
                  setEconomySeats(event.target.value);
                }}
              />
              <input
                type="number"
                placeholder="Business Seats"
                onChange={(event) => {
                  setBusinessSeats(event.target.value);
                }}
              />
              Departure Date
              <input
                type="date"
                onChange={(event) => {
                  setDepartureDate(event.target.value);
                }}
              />
              Arrival Date
              <input
                type="date"
                onChange={(event) => {
                  setArrivalDate(event.target.value);
                }}
              />
            </div>
          </div>
          <button onClick={creFlight}>Create</button>
        </div>
      </div>
    </div>
  );
}

export default Login;
