import { useState, useEffect } from "react";
import { useNavigate } from "react-router";
import axios from "axios";
import "../App.css";
import { Dialog } from "@material-ui/core";
import DialogTitle from "@material-ui/core/DialogTitle";
import DialogContent from "@material-ui/core/DialogContent";

function ReserveFlights() {
  const navigate = useNavigate();

  const [adults, setAdults] = useState(null);
  const [children, setChildren] = useState(null);
  const [departureAirport, setDepartureAirport] = useState(null);
  const [arrivalAirport, setArrivalAirport] = useState(null);
  const [departureDate, setDepartureDate] = useState(null);
  const [returnDate, setReturnDate] = useState(null);
  const [cabin, setCabin] = useState("numberofEconomySeats");

  const [listOfFlights, setListOfFlights] = useState([]);

  const [depFli, setDepFli] = useState("departure");

  const [openPopupMore, setOpenPopupMore] = useState(false);
  const [openPopupSum, setOpenPopupSum] = useState(false);

  const [flightID, setFlightID] = useState(null);

  const [dep, setDep] = useState(null);
  const [arr, setArr] = useState(null);

  const home = () => {
    navigate(-1);
  };

  const searchFlight = () => {
    if (
      adults == null ||
      children == null ||
      departureDate == null ||
      returnDate == null ||
      departureAirport == null ||
      arrivalAirport == null
    ) {
      alert("Please fill all fields!!!");
    } else {
      var val = {
        adults: adults,
        children: children,
        departureAirport: departureAirport,
        arrivalAirport: arrivalAirport,
        departureDate: departureDate,
        returnDate: returnDate,
        cabin: cabin,
      };
      axios
        .post("http://localhost:8000/admin/departureFlights", val)
        .then((result) => {
          setListOfFlights(result.data);
        })
        .catch((err) => {
          console.log(err);
        });
    }
    setDepFli("departure");
    setDepID(null);
    setArrID(null);
  };

  const chooseDep = (fli) => {
    if (dep == null) {
      setDep(fli);
      setDepFli("return");
      var val = {
        adults: adults,
        children: children,
        departureAirport: departureAirport,
        arrivalAirport: arrivalAirport,
        departureDate: departureDate,
        returnDate: returnDate,
        cabin: cabin,
      };
      axios
        .post("http://localhost:8000/admin/returnFlights", val)
        .then((result) => {
          setListOfFlights(result.data);
        })
        .catch((err) => {
          console.log(err);
        });
    } else {
      setArr(fli);
      setOpenPopupSum(true);
    }
  };

  useEffect(() => {
    const listener = (event) => {
      if (event.code === "Enter") {
        event.preventDefault();
        searchFlight();
      }
      if (event.code === "Escape") {
        event.preventDefault();
        home();
      }
    };
    document.addEventListener("keydown", listener);
    return () => {
      document.removeEventListener("keydown", listener);
    };
  });

  return (
    <div>
      <div className="HeaderContainer">
        <div className="HeaderButton">
          <button onClick={home}>Back</button>
        </div>
        <div className="SearchFlight">
          <h1>Search Flights</h1>
        </div>
      </div>
      <div className="App">
        <div className="Reserveinputs">
          <div className="ReserveinputsSub">
            <div>
              Number Of Children
              <input
                type="number"
                placeholder="Number Of Children"
                onChange={(event) => {
                  setChildren(event.target.value);
                }}
              />
              Number Of Adults
              <input
                type="number"
                placeholder="Number Of Adults"
                onChange={(event) => {
                  setAdults(event.target.value);
                }}
              />
              Departure Airport
              <input
                type="text"
                placeholder="Departure Airport"
                onChange={(event) => {
                  setDepartureAirport(event.target.value);
                }}
              />
            </div>
            <div>
              Arrival Airport
              <input
                type="text"
                placeholder="Arrival Airport"
                onChange={(event) => {
                  setArrivalAirport(event.target.value);
                }}
              />
              Departure Date
              <input
                type="date"
                onChange={(event) => {
                  setDepartureDate(event.target.value);
                }}
              />
              Return Date
              <input
                type="date"
                onChange={(event) => {
                  setReturnDate(event.target.value);
                }}
              />
            </div>
            <div className="cabinButtons">
              <label className="cabinButtons1">
                Economy Seats :
                <input
                  type="radio"
                  name="cabinClass"
                  value="numberofEconomySeats"
                  checked={cabin === "numberofEconomySeats"}
                  onChange={() => setCabin("numberofEconomySeats")}
                />
              </label>
              <label className="cabinButtons2">
                Business Seats :
                <input
                  type="radio"
                  name="cabinClass"
                  value="numberofBusinessSeats"
                  checked={cabin === "numberofBusinessSeats"}
                  onChange={() => setCabin("numberofBusinessSeats")}
                />
              </label>
            </div>
          </div>
          <button onClick={searchFlight}>Submit</button>
        </div>
      </div>
      <div className="listOfFlights">
        Choose a {depFli} flight from the below list
        {listOfFlights.map((val) => {
          return (
            <div className="flightContainerRes">
              <div>
                <div className="flightRes">
                  <h3 id="fliNumber">
                    Flight Number: <br />
                    {val.flightNumber}
                  </h3>
                  <h3>
                    Departure Time: <br />
                    {val.departureTime}
                  </h3>
                  <h3>
                    Arrival Time: <br />
                    {val.arrivalTime}
                  </h3>
                  <h3>
                    Trip Duration: <br />
                    {val.tripDuration + " Hours"}
                  </h3>
                  <h3>
                    Baggage Allowance: <br />
                    {val.baggage + " Bags"}
                  </h3>
                  <h3>
                    Price: <br />
                    {val.price + " L.E"}
                  </h3>
                </div>
              </div>
              <button
                onClick={() => {
                  setFlightID(val._id);
                  setOpenPopupMore(true);
                }}
              >
                More Info
              </button>
              <button
                id="chooseB"
                onClick={() => {
                  chooseDep(val);
                }}
              >
                Choose
              </button>
            </div>
          );
        })}
      </div>
      <Dialog open={openPopupMore} maxWidth="lg">
        <DialogTitle>
          <div className="PopupHeaderRes">
            Flight Info
            <button onClick={() => setOpenPopupMore(false)}>Close</button>
          </div>
        </DialogTitle>
        <DialogContent dividers>
          {listOfFlights.map((val) => {
            if (val._id == flightID) {
              return (
                <div>
                  <div className="flightPopup">
                    <h3 id="fliNumber">
                      Flight Number: <br />
                      {val.flightNumber}
                    </h3>
                    <h3>
                      Departure Time: <br />
                      {val.departureTime}
                    </h3>
                    <h3>
                      Departure Date: <br />
                      {val.departureDate}
                    </h3>
                    <h3>
                      Arrival Time: <br />
                      {val.arrivalTime}
                    </h3>
                    <h3>
                      Arrival Date: <br />
                      {val.arrivalDate}
                    </h3>
                    <h3>
                      Trip Duration: <br />
                      {val.tripDuration + " Hours"}
                    </h3>
                  </div>
                  <div className="flightPopup">
                    <h3 id="fliNumber">
                      Economy Seats: <br />
                      {val.numberofEconomySeats}
                    </h3>
                    <h3>
                      Business Seats: <br />
                      {val.numberofBusinessSeats}
                    </h3>
                    <h3>
                      Departure Airport: <br />
                      {val.departureAirport}
                    </h3>
                    <h3>
                      Arrival Airport: <br />
                      {val.arrivalAirport}
                    </h3>
                    <h3>
                      Baggage Allowance: <br />
                      {val.baggage + " Bags"}
                    </h3>
                    <h3>
                      Price: <br />
                      {val.price + " L.E"}
                    </h3>
                  </div>
                </div>
              );
            }
          })}
        </DialogContent>
      </Dialog>
      <Dialog open={openPopupSum} maxWidth="lg">
        <DialogTitle>
          <div className="PopupHeaderRes">
            Flight Info
            <button onClick={() => setOpenPopupSum(false)}>Close</button>
          </div>
        </DialogTitle>
        <DialogContent dividers>
          {listOfFlights.map((val) => {
            if (val._id == flightID) {
              return (
                <div>
                  <div className="flightPopup">
                    <h3 id="fliNumber">
                      Flight Number: <br />
                      {val.flightNumber}
                    </h3>
                    <h3>
                      Departure Time: <br />
                      {val.departureTime}
                    </h3>
                    <h3>
                      Departure Date: <br />
                      {val.departureDate}
                    </h3>
                    <h3>
                      Arrival Time: <br />
                      {val.arrivalTime}
                    </h3>
                    <h3>
                      Arrival Date: <br />
                      {val.arrivalDate}
                    </h3>
                    <h3>
                      Trip Duration: <br />
                      {val.tripDuration + " Hours"}
                    </h3>
                  </div>
                  <div className="flightPopup">
                    <h3 id="fliNumber">
                      Economy Seats: <br />
                      {val.numberofEconomySeats}
                    </h3>
                    <h3>
                      Business Seats: <br />
                      {val.numberofBusinessSeats}
                    </h3>
                    <h3>
                      Departure Airport: <br />
                      {val.departureAirport}
                    </h3>
                    <h3>
                      Arrival Airport: <br />
                      {val.arrivalAirport}
                    </h3>
                    <h3>
                      Baggage Allowance: <br />
                      {val.baggage + " Bags"}
                    </h3>
                    <h3>
                      Price: <br />
                      {val.price + " L.E"}
                    </h3>
                  </div>
                </div>
              );
            }
          })}
        </DialogContent>
      </Dialog>
    </div>
  );
}

export default ReserveFlights;
