import { useState, useEffect } from "react";
import { useNavigate } from "react-router";
import axios from "axios";
import "../App.css";
import { Dialog } from "@material-ui/core";
import DialogTitle from "@material-ui/core/DialogTitle";
import DialogContent from "@material-ui/core/DialogContent";

function CreateFlight() {
  const navigate = useNavigate();

  const [flightNumber, setFlightNumber] = useState(null);
  const [departureTime, setDepartureTime] = useState(null);
  const [arrivalTime, setArrivalTime] = useState(null);
  const [economySeats, setEconomySeats] = useState(null);
  const [arrivalDate, setArrivalDate] = useState(null);
  const [departureDate, setDepartureDate] = useState(null);
  const [businessSeats, setBusinessSeats] = useState(null);
  const [airport, setAirport] = useState(null);

  const [flightNumberU, setFlightNumberU] = useState(null);
  const [departureTimeU, setDepartureTimeU] = useState(null);
  const [arrivalTimeU, setArrivalTimeU] = useState(null);
  const [economySeatsU, setEconomySeatsU] = useState(null);
  const [arrivalDateU, setArrivalDateU] = useState(null);
  const [departureDateU, setDepartureDateU] = useState(null);
  const [businessSeatsU, setBusinessSeatsU] = useState(null);
  const [airportU, setAirportU] = useState(null);

  const [listOfFlights, setListOfFlights] = useState([]);

  const [openPopupCreate, setOpenPopupCreate] = useState(false);
  const [openPopupUpdate, setOpenPopupUpdate] = useState(false);

  const [upID, setUpID] = useState(null);

  const home = () => {
    navigate(-1);
  };

  const create = () => {
    if (
      flightNumber == null ||
      departureTime == null ||
      arrivalTime == null ||
      departureDate == null ||
      arrivalDate == null ||
      economySeats == null ||
      businessSeats == null ||
      airport == null
    ) {
      alert("Please fill all fields!!!");
    } else if (departureDate > arrivalDate) {
      alert("Departure date can't be after Arrival date!!!");
    } else if (departureDate == arrivalDate && departureTime >= arrivalTime) {
      alert("Departure time must be before Arrival time if on the same day!!!");
    } else {
      axios
        .post("http://localhost:8000/admin/createFlight", {
          flightNumber: flightNumber,
          departureTime: departureTime,
          arrivalTime: arrivalTime,
          numberofEconomySeats: economySeats,
          arrivalDate: arrivalDate,
          departureDate: departureDate,
          numberofBusinessSeats: businessSeats,
          airport: airport,
        })
        .then((result) => {
          if (result.data.name == "ValidationError") {
            alert("Flight number already exists!!!");
          } else {
            setOpenPopupCreate(false);
            setListOfFlights([
              ...listOfFlights,
              {
                flightNumber: flightNumber,
                departureTime: departureTime,
                arrivalTime: arrivalTime,
                numberofEconomySeats: economySeats,
                arrivalDate: arrivalDate,
                departureDate: departureDate,
                numberofBusinessSeats: businessSeats,
                airport: airport,
              },
            ]);
            setFlightNumber(null);
            setDepartureTime(null);
            setArrivalTime(null);
            setDepartureDate(null);
            setArrivalDate(null);
            setEconomySeats(null);
            setBusinessSeats(null);
            setAirport(null);
          }
        })
        .catch((err) => {});
    }
  };

  const updFlight = (id) => {
    if (window.confirm("Are you sure to update this Flight?")) {
      var val = {
        _id: id,
        flightNumber: flightNumberU,
        departureTime: departureTimeU,
        arrivalTime: arrivalTimeU,
        numberofEconomySeats: economySeatsU,
        arrivalDate: arrivalDateU,
        departureDate: departureDateU,
        numberofBusinessSeats: businessSeatsU,
        airport: airportU,
      };
      Object.keys(val).forEach(
        (k) => !val[k] && val[k] !== undefined && delete val[k]
      );
      axios
        .put("http://localhost:8000/admin/updateFlight", val)
        .then((result) => {
          if (result.data == "Error") {
            alert("Flight number already exists!!!");
          } else {
            setListOfFlights(
              listOfFlights.map((val) => {
                return val._id == id
                  ? {
                      _id: id,
                      flightNumber:
                        flightNumberU == null
                          ? val.flightNumber
                          : flightNumberU,
                      departureTime:
                        departureTimeU == null
                          ? val.departureTime
                          : departureTimeU,
                      arrivalTime:
                        arrivalTimeU == null ? val.arrivalTime : arrivalTimeU,
                      numberofEconomySeats:
                        economySeatsU == null
                          ? val.numberofEconomySeats
                          : economySeatsU,
                      arrivalDate:
                        arrivalDateU == null ? val.arrivalDate : arrivalDateU,
                      departureDate:
                        departureDateU == null
                          ? val.departureDate
                          : departureDateU,
                      numberofBusinessSeats:
                        businessSeatsU == null
                          ? val.numberofBusinessSeats
                          : businessSeatsU,
                      airport: airportU == null ? val.airport : airportU,
                    }
                  : val;
              })
            );
            setOpenPopupUpdate(false);
          }
        });
    }
  };

  useEffect(() => {
    const listener = (event) => {
      if (event.code === "Enter") {
        event.preventDefault();
        if (openPopupCreate == true) {
          create();
        }
      }
      if (event.code === "Escape") {
        event.preventDefault();
        home();
      }
    };
    document.addEventListener("keydown", listener);
    return () => {
      document.removeEventListener("keydown", listener);
    };
  });

  useEffect(() => {
    axios
      .get("http://localhost:8000/admin/showFlight")
      .then((result) => {
        setListOfFlights(result.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  return (
    <div>
      <div className="HeaderContainer">
        <div className="HeaderButton">
          <button onClick={home}>Home</button>
        </div>
        <div className="CreFlight">
          <h1>Create Flights</h1>
        </div>
      </div>
      <div className="App">
        <div className="Creinputs">
          <div>
            <div>
              <input
                type="number"
                placeholder="Flight Number"
                onChange={(event) => {
                  setFlightNumber(event.target.value);
                }}
              />
              Departure Time
              <input
                type="time"
                onChange={(event) => {
                  setDepartureTime(event.target.value);
                }}
              />
              Arrival Time
              <input
                type="time"
                onChange={(event) => {
                  setArrivalTime(event.target.value);
                }}
              />
              <input
                type="text"
                placeholder="Airport"
                onChange={(event) => {
                  setAirport(event.target.value);
                }}
              />
            </div>
            <div>
              <input
                type="number"
                placeholder="Economy Seats"
                onChange={(event) => {
                  setEconomySeats(event.target.value);
                }}
              />
              <input
                type="number"
                placeholder="Business Seats"
                onChange={(event) => {
                  setBusinessSeats(event.target.value);
                }}
              />
              Departure Date
              <input
                type="date"
                onChange={(event) => {
                  setDepartureDate(event.target.value);
                }}
              />
              Arrival Date
              <input
                type="date"
                onChange={(event) => {
                  setArrivalDate(event.target.value);
                }}
              />
            </div>
          </div>
          <button onClick={() => setOpenPopupCreate(true)}>Create</button>
        </div>
      </div>
      <div className="listOfFlights">
        {listOfFlights.map((val) => {
          return (
            <div className="flightContainer">
              <div className="flight">
                <h3 id="fliNumber">
                  Flight Number: <br />
                  {val.flightNumber}
                </h3>
                <h3>
                  Departure Time: <br />
                  {val.departureTime}
                </h3>
                <h3>
                  Departure Date: <br />
                  {val.departureDate}
                </h3>
                <h3>
                  Arrival Time: <br />
                  {val.arrivalTime}
                </h3>
                <h3>
                  Arrival Date: <br />
                  {val.arrivalDate}
                </h3>
                <h3>
                  Economy Seats: <br />
                  {val.numberofEconomySeats}
                </h3>
                <h3>
                  Business Seats: <br />
                  {val.numberofBusinessSeats}
                </h3>
                <h3>
                  Airport: <br />
                  {val.airport}
                </h3>
              </div>
              <button
                onClick={() => {
                  console.log(upID);
                  setUpID(val._id);
                  setOpenPopupUpdate(true);
                  console.log(upID);
                }}
              >
                Update
              </button>
            </div>
          );
        })}
      </div>
      <Dialog open={openPopupCreate} maxWidth="lg">
        <DialogTitle>
          <div className="PopupHeader">
            Create Flight
            <button onClick={() => setOpenPopupCreate(false)}>Close</button>
          </div>
        </DialogTitle>
        <DialogContent dividers>
          <div className="Popupinputs">
            <div>
              <div>
                Flight Number
                <input
                  type="number"
                  placeholder="Flight Number"
                  onChange={(event) => {
                    setFlightNumber(event.target.value);
                  }}
                />
                Airport
                <input
                  type="text"
                  placeholder="Airport"
                  onChange={(event) => {
                    setAirport(event.target.value);
                  }}
                />
              </div>
              <div>
                Departure Time
                <input
                  type="time"
                  onChange={(event) => {
                    setDepartureTime(event.target.value);
                  }}
                />
                Arrival Time
                <input
                  type="time"
                  onChange={(event) => {
                    setArrivalTime(event.target.value);
                  }}
                />
              </div>
              <div>
                Departure Date
                <input
                  type="date"
                  onChange={(event) => {
                    setDepartureDate(event.target.value);
                  }}
                />
                Arrival Date
                <input
                  type="date"
                  onChange={(event) => {
                    setArrivalDate(event.target.value);
                  }}
                />
              </div>
              <div>
                Economy Seats
                <input
                  type="number"
                  placeholder="Economy Seats"
                  onChange={(event) => {
                    setEconomySeats(event.target.value);
                  }}
                />
                Business Seats
                <input
                  type="number"
                  placeholder="Business Seats"
                  onChange={(event) => {
                    setBusinessSeats(event.target.value);
                  }}
                />
              </div>
            </div>
            <button onClick={create}>Create</button>
          </div>
        </DialogContent>
      </Dialog>
      <Dialog open={openPopupUpdate} maxWidth="lg">
        <DialogTitle>
          <div className="PopupHeader">
            Update Flight
            <button onClick={() => setOpenPopupUpdate(false)}>Close</button>
          </div>
        </DialogTitle>
        <DialogContent dividers>
          <div className="Popupinputs">
            <div>
              <div>
                Flight Number
                <input
                  type="number"
                  placeholder="Flight Number"
                  onChange={(event) => {
                    setFlightNumberU(event.target.value);
                  }}
                />
                Airport
                <input
                  type="text"
                  placeholder="Airport"
                  onChange={(event) => {
                    setAirportU(event.target.value);
                  }}
                />
              </div>
              <div>
                Departure Time
                <input
                  type="time"
                  onChange={(event) => {
                    setDepartureTimeU(event.target.value);
                  }}
                />
                Arrival Time
                <input
                  type="time"
                  onChange={(event) => {
                    setArrivalTimeU(event.target.value);
                  }}
                />
              </div>
              <div>
                Departure Date
                <input
                  type="date"
                  onChange={(event) => {
                    setDepartureDateU(event.target.value);
                  }}
                />
                Arrival Date
                <input
                  type="date"
                  onChange={(event) => {
                    setArrivalDateU(event.target.value);
                  }}
                />
              </div>
              <div>
                Economy Seats
                <input
                  type="number"
                  placeholder="Economy Seats"
                  onChange={(event) => {
                    setEconomySeatsU(event.target.value);
                  }}
                />
                Business Seats
                <input
                  type="number"
                  placeholder="Business Seats"
                  onChange={(event) => {
                    setBusinessSeatsU(event.target.value);
                  }}
                />
              </div>
            </div>
            <button
              onClick={() => {
                updFlight(upID);
              }}
            >
              Update
            </button>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

export default CreateFlight;
