import { useState, useEffect } from "react";
import { useNavigate } from "react-router";
import axios from "axios";
import "../App.css";
import { Dialog } from "@material-ui/core";
import DialogTitle from "@material-ui/core/DialogTitle";
import DialogContent from "@material-ui/core/DialogContent";

function CreateFlight() {
  const navigate = useNavigate();

  const [flightNumber, setFlightNumber] = useState(null);
  const [departureTime, setDepartureTime] = useState(null);
  const [arrivalTime, setArrivalTime] = useState(null);
  const [economySeats, setEconomySeats] = useState(null);
  const [arrivalDate, setArrivalDate] = useState(null);
  const [departureDate, setDepartureDate] = useState(null);
  const [businessSeats, setBusinessSeats] = useState(null);
  const [airport, setAirport] = useState(null);

  const [listOfFlights, setListOfFlights] = useState([]);

  const [openPopup, setOpenPopup] = useState(false);

  const home = () => {
    navigate(-1);
  };

  const create = () => {
    if (
      flightNumber == null ||
      departureTime == null ||
      arrivalTime == null ||
      departureDate == null ||
      arrivalDate == null ||
      economySeats == null ||
      businessSeats == null ||
      airport == null
    ) {
      alert("Please fill all fields!!!");
    } else {
      axios
        .post("http://localhost:8000/admin/createFlight", {
          flightNumber: flightNumber,
          departureTime: departureTime,
          arrivalTime: arrivalTime,
          numberofEconomySeats: economySeats,
          arrivalDate: arrivalDate,
          departureDate: departureDate,
          numberofBusinessSeats: businessSeats,
          airport: airport,
        })
        .then(() => {
          setListOfFlights([
            ...listOfFlights,
            {
              flightNumber: flightNumber,
              departureTime: departureTime,
              arrivalTime: arrivalTime,
              numberofEconomySeats: economySeats,
              arrivalDate: arrivalDate,
              departureDate: departureDate,
              numberofBusinessSeats: businessSeats,
              airport: airport,
            },
          ]);
        })
        .catch((err) => {});
    }
  };

  useEffect(() => {
    const listener = (event) => {
      if (event.code === "Enter") {
        event.preventDefault();
        create();
      }
      if (event.code === "Escape") {
        event.preventDefault();
        home();
      }
    };
    document.addEventListener("keydown", listener);
    return () => {
      document.removeEventListener("keydown", listener);
    };
  });

  useEffect(() => {
    axios
      .get("http://localhost:8000/admin/showFlight")
      .then((result) => {
        setListOfFlights(result.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  return (
    <div>
      <div className="HeaderContainer">
        <div className="HeaderButton">
          <button onClick={home}>Home</button>
        </div>
        <div className="CreFlight">
          <h1>Create Flights</h1>
        </div>
      </div>
      <div className="App">
        <div className="Creinputs">
          <div>
            <div>
              <input
                type="number"
                placeholder="Flight Number"
                onChange={(event) => {
                  setFlightNumber(event.target.value);
                }}
              />
              Departure Time
              <input
                type="time"
                onChange={(event) => {
                  setDepartureTime(event.target.value);
                }}
              />
              Arrival Time
              <input
                type="time"
                onChange={(event) => {
                  setArrivalTime(event.target.value);
                }}
              />
              <input
                type="text"
                placeholder="Airport"
                onChange={(event) => {
                  setAirport(event.target.value);
                }}
              />
            </div>
            <div>
              <input
                type="number"
                placeholder="Economy Seats"
                onChange={(event) => {
                  setEconomySeats(event.target.value);
                }}
              />
              <input
                type="number"
                placeholder="Business Seats"
                onChange={(event) => {
                  setBusinessSeats(event.target.value);
                }}
              />
              Departure Date
              <input
                type="date"
                onChange={(event) => {
                  setDepartureDate(event.target.value);
                }}
              />
              Arrival Date
              <input
                type="date"
                onChange={(event) => {
                  setArrivalDate(event.target.value);
                }}
              />
            </div>
          </div>
          <button onClick={() => setOpenPopup(true)}>Create</button>
        </div>
      </div>
      <div className="listOfFlights">
        {listOfFlights.map((val) => {
          return (
            <div className="flight">
              <h3 id="fliNumber">
                Flight Number: <br />
                {val.flightNumber}
              </h3>
              <h3>
                Departure Time: <br />
                {val.departureTime}
              </h3>
              <h3>
                Departure Date: <br />
                {val.departureDate}
              </h3>
              <h3>
                Arrival Time: <br />
                {val.arrivalTime}
              </h3>
              <h3>
                Arrival Date: <br />
                {val.arrivalDate}
              </h3>
              <h3>
                Economy Seats: <br />
                {val.numberofEconomySeats}
              </h3>
              <h3>
                Business Seats: <br />
                {val.numberofBusinessSeats}
              </h3>
              <h3>
                Airport: <br />
                {val.airport}
              </h3>
            </div>
          );
        })}
      </div>
      <Dialog open={openPopup} maxWidth="lg">
        <DialogTitle>
          <div className="PopupHeader">
            Flight Form
            <button onClick={() => setOpenPopup(false)}>Close</button>
          </div>
        </DialogTitle>
        <DialogContent dividers>
          <div className="Popupinputs">
            <div>
              <div>
                Flight Number
                <input
                  type="number"
                  placeholder="Flight Number"
                  onChange={(event) => {
                    setFlightNumber(event.target.value);
                  }}
                />
                Airport
                <input
                  type="text"
                  placeholder="Airport"
                  onChange={(event) => {
                    setAirport(event.target.value);
                  }}
                />
              </div>
              <div>
                Departure Time
                <input
                  type="time"
                  onChange={(event) => {
                    setDepartureTime(event.target.value);
                  }}
                />
                Arrival Time
                <input
                  type="time"
                  onChange={(event) => {
                    setArrivalTime(event.target.value);
                  }}
                />
              </div>
              <div>
                Departure Date
                <input
                  type="date"
                  onChange={(event) => {
                    setDepartureDate(event.target.value);
                  }}
                />
                Arrival Date
                <input
                  type="date"
                  onChange={(event) => {
                    setArrivalDate(event.target.value);
                  }}
                />
              </div>
              <div>
                Economy Seats
                <input
                  type="number"
                  placeholder="Economy Seats"
                  onChange={(event) => {
                    setEconomySeats(event.target.value);
                  }}
                />
                Business Seats
                <input
                  type="number"
                  placeholder="Business Seats"
                  onChange={(event) => {
                    setBusinessSeats(event.target.value);
                  }}
                />
              </div>
            </div>
            <button onClick={create}>Create</button>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

export default CreateFlight;
