import { useState, useEffect } from "react";
import { useNavigate } from "react-router";
import axios from "axios";
import "../App.css";

function ReserveFlights() {
  const navigate = useNavigate();

  const [adults, setAdults] = useState(null);
  const [children, setChildren] = useState(null);
  const [departureDate, setDepartureDate] = useState(null);
  const [arrivalDate, setArrivalDate] = useState(null);
  const [cabin, setCabin] = useState(null);

  const [listOfFlights, setListOfFlights] = useState([]);

  const home = () => {
    navigate(-1);
  };

  const searchFlight = () => {
    var val = {
      adults: adults,
      children: children,
      departureDate: departureDate,
      arrivalDate: arrivalDate,
      cabin: cabin,
    };
    Object.keys(val).forEach(
      (k) => !val[k] && val[k] !== undefined && delete val[k]
    );
    axios
      .post("http://localhost:8000/admin/searchForFlights", val)
      .then((result) => {
        setListOfFlights(result.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  useEffect(() => {
    const listener = (event) => {
      if (event.code === "Enter") {
        event.preventDefault();
        searchFlight();
      }
      if (event.code === "Escape") {
        event.preventDefault();
        home();
      }
    };
    document.addEventListener("keydown", listener);
    return () => {
      document.removeEventListener("keydown", listener);
    };
  });

  return (
    <div>
      <div className="HeaderContainer">
        <div className="HeaderButton">
          <button onClick={home}>Back</button>
        </div>
        <div className="SearchFlight">
          <h1>Search Flights</h1>
        </div>
      </div>
      <div className="App">
        <div className="Reserveinputs">
          <div className="ReserveinputsSub">
            <div>
              Number Of Children
              <input
                type="number"
                placeholder="Number Of Children"
                onChange={(event) => {
                  setChildren(event.target.value);
                }}
              />
              Number Of Adults
              <input
                type="number"
                placeholder="Number Of Adults"
                onChange={(event) => {
                  setAdults(event.target.value);
                }}
              />
              Departure Airport
              <input
                type="text"
                placeholder="Departure Airport"
                // onChange={(event) => {
                //   setDepartureAirport(event.target.value);
                // }}
              />
            </div>
            <div>
              Arrival Airport
              <input
                type="text"
                placeholder="Arrival Airport"
                // onChange={(event) => {
                //   setArrivalAirport(event.target.value);
                // }}
              />
              Departure Date
              <input
                type="date"
                onChange={(event) => {
                  setDepartureDate(event.target.value);
                }}
              />
              Arrival Date
              <input
                type="date"
                onChange={(event) => {
                  setArrivalDate(event.target.value);
                }}
              />
            </div>
            <div>
              Cabin
              <label>
                <input
                  type="radio"
                  name="gender"
                  value="male"
                  checked={state.gender === "male"}
                  onChange={handleChange}
                />{" "}
                Male
              </label>
              <label>
                <input
                  type="radio"
                  name="gender"
                  value="female"
                  checked={state.gender === "female"}
                  onChange={handleChange}
                />{" "}
                Female
              </label>
            </div>
          </div>
          <button onClick={searchFlight}>Submit</button>
        </div>
      </div>
      <div className="listOfFlights">
        {listOfFlights.map((val) => {
          return (
            <div className="flight">
              <h3 id="fliNumber">
                Flight Number: <br />
                {val.flightNumber}
              </h3>
              <h3>
                Departure Time: <br />
                {val.departureTime}
              </h3>
              <h3>
                Departure Date: <br />
                {val.departureDate}
              </h3>
              <h3>
                Arrival Time: <br />
                {val.arrivalTime}
              </h3>
              <h3>
                Arrival Date: <br />
                {val.arrivalDate}
              </h3>
              <h3>
                Economy Seats: <br />
                {val.numberofEconomySeats}
              </h3>
              <h3>
                Business Seats: <br />
                {val.numberofBusinessSeats}
              </h3>
              <h3>
                Airport: <br />
                {val.airport}
              </h3>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default ReserveFlights;
